import "./Login.css";
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";






const customTextFieldStyle = {
  width: '400px',             // Adjust the width to your desired length
  borderColor: 'blue',
  position:'relative',
  top:'380px' ,
  left:'30px'     // Set the outline color to blue
};

const Login = ({ onClose }) => {



  const [data, setData] = useState({ email: "", password: "" });
	const [error, setError] = useState("");

	const handleChange = ({ currentTarget: input }) => {
		setData({ ...data, [input.name]: input.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const url = "http://localhost:8080/api/auth";
			const { data: res } = await axios.post(url, data);
			localStorage.setItem("token", res.data);
			window.location = "/";
		} catch (error) {
			if (
				error.response &&
				error.response.status >= 400 &&
				error.response.status <= 500
			) {
				setError(error.response.data.message);
			}
		}

  return (
    <div className="login1">
      <div className="login-child" />
      <img className="icon2" alt="" src="/undefined141.png" />
      <div className="login2">Login</div>

      <Button variant="contained" sx={{position:"relative",top:"500px",left:"180px"}}>Sign in</Button>
      <h1> New here </h1>
      <Button variant="contained" sx={{position:"relative",top:"500px",left:"180px"}}>Sign in</Button>
      <TextField
      label="Phone Number"
      variant="outlined"
      style={customTextFieldStyle}
    />

    </div>





  );
};

}

export default Login;




